(function(){
    
    let productAccordions = document.querySelectorAll('.product-accordion');

    [].forEach.call(productAccordions, function(accordion) {

        let items = accordion.querySelectorAll('.product-accordion__item');
        
        [].forEach.call(items, function(item) {
            item.classList.add('is--collapsed');

            let clickElem = item.querySelector('.product-accordion__title');

            clickElem.addEventListener('click', function(evt) {
                item.classList.toggle('is--collapsed');
            })
        })

    });

})();